con:config.script:
import com.eviware.soapui.support.GroovyUtils
//to get the test case name
def testCaseName = context.getTestCase().name
log.info testCaseName

//tp get the test suite name
def testSuiteName = context.getTestCase().getTestSuite().name
log.info testSuiteName

def reqxml = context.expand('${NewCustomer#request}')
log.info reqxml

def email = testRunner.testCase.getPropertyValue("Email")
log.info email

def firstname = testRunner.testCase.getPropertyValue("FirstName")
log.info firstname

def surname = testRunner.testCase.getPropertyValue("Surname")
log.info surname

def groovyUtils = new GroovyUtils(context)
xmlholder = groovyUtils.getXmlHolder(reqxml)

xmlholder.setNodeValue("//customer/email", email)
xmlholder.setNodeValue("//customer/firstname", firstname)
xmlholder.setNodeValue("//customer/lastname", surname)

def updatedxml = xmlholder.getXml()
log.info updatedxml

testRunner.testCase.getTestStepByName("NewCustomer").setPropertyValue("request", updatedxml)

// to run the new customer test step
testRunner.testCase.testSuite.project.testSuites[testSuiteName].testCases[testCaseName].testSteps["NewCustomer"].run(testRunner, context)

//to get the response xml
def resp = context.expand('${NewCustomer#response}')
log.info resp

//to parse the response xml
def resxmlholdr = groovyUtils.getXmlHolder(resp)

//to get the node value of customer id
def customerid = resxmlholdr.getNodeValue("//customerId")
log.info customerid

//to run the GetList TestStep
testRunner.testCase.testSuite.project.testSuites[testSuiteName].testCases[testCaseName].testSteps["GetList"].run(testRunner, context)

// to get the request xml for another request GetBYID
def reqxml1 = context.expand('${GetByID#request}')
log.info reqxml1

//to parse the GetByID xml
xmlholder1 = groovyUtils.getXmlHolder(reqxml1)

//to set the node value ofGetBYID for customer id from response xml of new customer
xmlholder1.setNodeValue("//customerId", customerid)

def updatedxml1 = xmlholder1.getXml()
log.info updatedxml1

testRunner.testCase.getTestStepByName("GetByID").setPropertyValue("request", updatedxml1)

// to run the GetByID test step
testRunner.testCase.testSuite.project.testSuites[testSuiteName].testCases[testCaseName].testSteps["GetByID"].run(testRunner, context)

// to get the request xml for another request DeleteCustomer
def reqxml2 = context.expand('${DeleteByID#request}')
log.info reqxml2

//to parse the DeleteCustomer xml
xmlholder2 = groovyUtils.getXmlHolder(reqxml2)

//to set the node value of DeleteCustomer for customer id from response xml of new customer
xmlholder2.setNodeValue("//customerId", customerid)

def updatedxml2 = xmlholder2.getXml()
log.info updatedxml2

testRunner.testCase.getTestStepByName("DeleteByID").setPropertyValue("request", updatedxml2)

// to run the GetByID test step
testRunner.testCase.testSuite.project.testSuites[testSuiteName].testCases[testCaseName].testSteps["DeleteByID"].run(testRunner, context)
